mapper.select:
SELECT SUM(nownum) num, SUM(yesterdayNum) sevenNum, SUM(passNum) passNum, SUM(unpassNum) unpassNum, #{id} id, userName FROM ( SELECT SUM(`shop_num`) nownum, 0 yesterdayNum, 0 passNum, 0 unpassNum, dr.interface_id id, pu.user_name userName FROM delivery_receitp dr LEFT JOIN person_user pu ON pu.id = dr.interface_id WHERE dr.`status` = 1 AND pu.`status` = 1 AND dr.isvxvip != 1 and dr.`city_id` = #{cityId} AND DATE_FORMAT(dr.submit_time,'%Y-%m-%d') = DATE_FORMAT(NOW(),'%Y-%m-%d') AND dr.interface_id = #{id}
UNION ALL
SELECT 0 nownum, SUM(`shop_num`) yesterdayNum, 0 passNum, 0 unpassNum, dr.interface_id id, pu.user_name userName FROM delivery_receitp dr LEFT JOIN person_user pu ON pu.id = dr.interface_id WHERE dr.`status` = 1 AND pu.`status` = 1 AND dr.isvxvip != 1 and dr.`city_id` = #{cityId} AND DATEDIFF(DATE_FORMAT(NOW(),'%Y-%m-%d'), DATE_FORMAT(dr.submit_time,'%Y-%m-%d')) = 1 AND dr.interface_id = #{id}
UNION ALL
SELECT 0, 0, SUM(`shop_num`) passNum, 0, dr.interface_id id, pu.user_name userName FROM delivery_receitp dr LEFT JOIN person_user pu ON pu.id = dr.interface_id WHERE dr.`status` = 1 AND pu.`status` = 1 and dr.`city_id` = #{cityId} AND dr.isvxvip != 1 AND dr.`interface_id` = #{id} AND DATE_FORMAT(dr.submit_time,'%Y-%m') = DATE_FORMAT(NOW(),'%Y-%m')
UNION ALL
SELECT 0, 0, 0, SUM(unqualifiedNum), dr.interface_id id, pu.user_name userName FROM delivery_receitp dr LEFT JOIN person_user pu ON pu.id = dr.interface_id WHERE dr.`status` = 1 AND pu.`status` = 1 AND dr.isvxvip != 1 and dr.`city_id` = #{cityId} AND dr.unqualifiedNum > 0 AND dr.`interface_id` = #{id} AND DATE_FORMAT(dr.submit_time,'%Y-%m') = DATE_FORMAT(NOW(),'%Y-%m')
) t
